var assert = require('assert');
var products = require('./eccommerce.js');

describe('Products', function() {
  it('fake products is an array to length 4',
    function() {
          assert.equal(products.productNow().length, 4);
  });

  it('delete products work',
    function() {
        products.delet(1);
        assert.equal(products.productNow().length, 3);
  });

  it('add products work',
    function() {
        products.insert({product:'Samsung Galaxy', amount:2});
        assert.equal(products.productNow().length, 4);
  });

  it('buy products work',
    function() {
        products.buy(1, 'pippo');
        assert.equal(products.productNow()[0].amount, 2);
        assert.equal(products.productAcquired().length, 1);
  });
});
